import '../../assets/css/admin.css';
import { useState, useEffect } from 'react';

import AdminNav from '../../components/Nav/adminNav';
import React, { Fragment,Component } from 'react'
import {Container, Row, Col} from 'react-bootstrap';

const initialState = {
  name: '',
  email: '',
  password: '',
  type: '',
  isMember: true,
};

export class addAdmin extends Component {

  render() {
    const navigate = useNavigate();
    const [values, setValues] = useState(initialState);
    const { user, isLoading, showAlert, displayAlert, setupUser } =
      useAppContext();

    const toggleMember = () => {
      setValues({ ...values, isMember: !values.isMember });
    };

    const handleChange = (e) => {
      setValues({ ...values, [e.target.name]: e.target.value });
    };
    const onSubmit = (e) => {
      e.preventDefault();
      const { name, email, password, isMember } = values;
      if (!email || !password || (!isMember && !name)) {
        displayAlert();
        return;
      }
      const currentUser = { name, email, password };
      if (isMember) {
        setupUser({
          currentUser,
          endPoint: 'login',
          alertText: 'Login Successful! Redirecting...',
        });
      } else {
        setupUser({
          currentUser,
          endPoint: 'register',
          alertText: 'User Created! Redirecting...',
        });
      }
    };

    useEffect(() => {
      if (user) {
        setTimeout(() => {
          navigate('/');
        }, 3000);
      }
    }, [user, navigate]);
    return (
      <Fragment>
        <AdminNav />
        <Container className='bg-white rounded p-3' >
          <Row>
            <Col>
              <p className='mb-1'>Name:</p>
              <input className='inputStyling' type="" name="" value="" />
              <p className='mb-1'>Email:</p>
              <input className='inputStyling' type="" name="" value="" />
              <p className='mb-1'>Phone Number:</p>
              <input className='inputStyling' type="" name="" value="" />
              <p className='mb-1'>Type of the admin:</p>
              <select className='inputStyling' name="cars" placeholder='Status'>
                <option value="">Sub-Admin</option>
                <option value="">Main Admin</option>
              </select>
              <button type="button" className='btn login'>Add admin</button>
            </Col>
          </Row>
        </Container>
      </Fragment>
    )
  }
}

export default addAdmin
