import { create } from "zustand";
import axios from "axios";

const AdminOperations = create((set) => ({
  admins: null,

  createForm: {
    name: "",
    email: "",
    phone: "",
    password: "",
    type: "",
  },

  updateForm: {
    _id: null,
    title: "",
    body: "",
  },

  fetchAdmins: async () => {
    // Fetch the notes
    const res = await axios.get("http://localhost:4000/api/v1/admins");

    // Set to state
    set({ admins: res.data.admins });
  },

  updateCreateFormField: (e) => {
    const { name, value } = e.target;

    set((state) => {
      return {
        createForm: {
          ...state.createForm,
          [name]: value,
        },
      };
    });
  },

  createNote: async (e) => {
    e.preventDefault();

    const { createForm, admins } = AdminOperations.getState();
    const res = await axios.post("http://localhost:4000/api/v1/registerAdmin", createForm);

    set({
      admins: [...admins, res.data.admin],
      createForm: {
        name: "",
        email: "",
        phone: "",
        password: "",
        type: "",
      },
    });
  },

  deleteNote: async (_id) => {
    // Delete the note
    const res = await axios.delete(`http://localhost:4000/api/v1/admins/${_id}`);
    const { notes } = AdminOperations.getState();

    // Update state
    const newNotes = notes.filter((note) => {
      return note._id !== _id;
    });

    set({ notes: newNotes });
  },

  handleUpdateFieldChange: (e) => {
    const { value, name } = e.target;

    set((state) => {
      return {
        updateForm: {
          ...state.updateForm,
          [name]: value,
        },
      };
    });
  },

  toggleUpdate: ({ _id, title, body }) => {
    set({
      updateForm: {
        title,
        body,
        _id,
      },
    });
  },

  updateNote: async (e) => {
    e.preventDefault();

    const {
      updateForm: { title, body, _id },
      notes,
    } = AdminOperations.getState();

    // Send the update request
    const res = await axios.put(`http://localhost:4000/api/v1/admins/${_id}`, {
      title,
      body,
    });

    // Update state
    const newNotes = [...notes];
    const noteIndex = notes.findIndex((note) => {
      return note._id === _id;
    });
    newNotes[noteIndex] = res.data.note;

    set({
      notes: newNotes,
      updateForm: {
        _id: null,
        title: "",
        body: "",
      },
    });
  },
}));

export default AdminOperations;
