function showCompanySignIn() {
    var hide = document.querySelector("#routeBox");
    var show = document.querySelector("#hideCompanySignIn");
    descriptionButton.style.backgroundColor = "#FFAA00";
    reviewButton.style.backgroundColor = "white";
    hide.style.display = "none";
    show.style.display = "block";
    show.style.animation = 'fadeIn 2s 1';
    show.style.opacity = "100%";
}
